@import url('https://fonts.googleapis.com/css2?family=Arial:wght@400;700&display=swap');

* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: Arial, sans-serif;
    font-size: 9pt;
    color: #000;
    line-height: 1.3;
}

.invoice-container {
    max-width: 210mm;
    margin: 0 auto;
    padding: 10px 15px;
    background: #fff;
}

.header {
    display: flex;
    justify-content: space-between;
    align-items: flex-start;
    margin-bottom: 12px;
    padding-bottom: 8px;
    border-bottom: 1px solid #ddd;
}

.company-section {
    flex: 1;
    display: flex;
    flex-direction: column;
    gap: 8px;
}

.company-logo {
    flex: 0 0 auto;
}

.company-logo img {
    max-width: 80px;
    max-height: 80px;
    height: auto;
    object-fit: contain;
}

.company-details {
    flex: 0 0 auto;
    font-size: 8.5pt;
    line-height: 1.4;
}

.company-name {
    font-weight: bold;
    font-size: 10pt;
    margin-bottom: 3px;
    text-transform: uppercase;
}

.invoice-title-section {
    text-align: right;
    flex: 0 0 auto;
    align-self: flex-start;
}

.invoice-title {
    font-size: 26pt;
    font-weight: bold;
    margin-bottom: 2px;
    letter-spacing: 1px;
    line-height: 1;
}

.invoice-number {
    font-size: 9pt;
    color: #333;
}

.bill-to-section {
    display: flex;
    justify-content: space-between;
    margin-bottom: 12px;
    padding: 8px 0;
}

.bill-to {
    flex: 1;
    font-size: 8.5pt;
    line-height: 1.4;
}

.bill-to-label {
    font-weight: bold;
    margin-bottom: 3px;
    font-size: 9pt;
}

.invoice-dates {
    text-align: right;
    flex: 0 0 200px;
    font-size: 8.5pt;
}

.date-row {
    display: flex;
    justify-content: space-between;
    margin-bottom: 4px;
    gap: 15px;
}

.date-label {
    font-weight: normal;
    text-align: left;
}

.date-value {
    font-weight: bold;
    text-align: right;
    min-width: 110px;
}

.items-table {
    width: 100%;
    border-collapse: collapse;
    margin-bottom: 10px;
    font-size: 8.5pt;
}

.items-table thead {
    background-color: #000;
    color: #fff;
}

.items-table th {
    padding: 6px 5px;
    text-align: left;
    font-weight: bold;
    font-size: 9pt;
}

.items-table th.text-right,
.items-table td.text-right {
    text-align: right;
}

.items-table td {
    padding: 8px 5px;
    border-bottom: 1px solid #ddd;
    vertical-align: top;
}

.items-table tbody tr:last-child td {
    border-bottom: 2px solid #000;
}

.tax-summary {
    margin-top: 15px;
    margin-left: auto;
    width: 350px;
    font-size: 8.5pt;
}

.tax-row {
    display: flex;
    justify-content: space-between;
    padding: 5px 8px;
    border-bottom: 1px solid #e0e0e0;
}

.tax-row.total {
    border-top: 2px solid #000;
    border-bottom: 2px solid #000;
    font-weight: bold;
    font-size: 10pt;
    margin-top: 6px;
    padding: 8px;
    background-color: #f5f5f5;
}

.footer {
    margin-top: 50px;
    text-align: right;
}

.for-company {
    font-weight: bold;
    margin-bottom: 40px;
    text-align: right;
    font-size: 9pt;
}

.signature-space {
    height: 60px;
    margin-bottom: 5px;
}

.signature-line {
    border-bottom: 2px solid #000;
    width: 200px;
    margin-left: auto;
    margin-bottom: 5px;
}

.signatory-name {
    font-size: 8.5pt;
    font-weight: normal;
}

@media print {
    body {
        margin: 0;
        padding: 0;
    }

    .invoice-container {
        max-width: 100%;
        padding: 5mm 10mm;
    }

    .header {
        margin-bottom: 10px;
        padding-bottom: 5px;
    }

    .footer {
        margin-top: 40px;
    }

    @page {
        margin: 10mm;
    }
}

