{# Fetch all company data in one query #}
{% set company_info = frappe.db.get_value("Company", doc.company, ["company_logo", "tax_id"], as_dict=1) or {} %}

{# Fetch company billing address #}
{% set comp_addr = frappe.db.get_value("Address", doc.billing_address, ["address_line1", "address_line2", "city", "state", "country", "pincode"], as_dict=1) if doc.billing_address else {} %}

{# Fetch supplier address #}
{% set supp_addr = frappe.db.get_value("Address", doc.supplier_address, ["address_line1", "address_line2", "city", "state", "country", "pincode"], as_dict=1) if doc.supplier_address else {} %}

{# Get supplier GSTIN if available #}
{% set supplier_gstin = frappe.db.get_value("Supplier", doc.supplier, "tax_id") %}

<div class="invoice-container">
    <!-- Header Section - SUPPLIER DETAILS -->
    <div class="header">
        {# LEFT SIDE: Supplier Logo (if available) + Details #}
        <div class="company-section">
            <div class="company-details">
                <div class="company-name">{{ doc.supplier_name }}</div>

                {% if supp_addr %}
                    {% if supp_addr.get('address_line1') %}<div>{{ supp_addr.address_line1 }}</div>{% endif %}
                    {% if supp_addr.get('address_line2') %}<div>{{ supp_addr.address_line2 }}</div>{% endif %}
                    {% if supp_addr.get('city') or supp_addr.get('pincode') %}
                    <div>
                        {%- if supp_addr.get('city') -%}{{ supp_addr.city }}{%- endif -%}
                        {%- if supp_addr.get('pincode') -%} {{ supp_addr.pincode }}{%- endif -%}
                    </div>
                    {% endif %}
                    {% if supp_addr.get('state') %}<div>{{ supp_addr.state }}</div>{% endif %}
                    {% if supp_addr.get('country') %}<div>{{ supp_addr.country }}</div>{% endif %}
                {% endif %}

                {% if supplier_gstin %}
                <div><strong>GSTIN:</strong> {{ supplier_gstin }}</div>
                {% endif %}
            </div>
        </div>

        {# RIGHT SIDE: Tax Invoice Title #}
        <div class="invoice-title-section">
            <div class="invoice-title">TAX INVOICE</div>
            <div class="invoice-number">Invoice# {{ doc.bill_no or doc.name }}</div>
        </div>
    </div>

    <!-- Bill To Section - COMPANY DETAILS -->
    <div class="bill-to-section">
        <div class="bill-to">
            <div class="bill-to-label">Bill To</div>
            <div><strong>{{ doc.company }}</strong></div>

            {% if comp_addr %}
                {% if comp_addr.get('address_line1') %}<div>{{ comp_addr.address_line1 }}</div>{% endif %}
                {% if comp_addr.get('address_line2') %}<div>{{ comp_addr.address_line2 }}</div>{% endif %}
                <div>
                    {%- if comp_addr.get('city') -%}{{ comp_addr.city }}{%- endif -%}
                    {%- if comp_addr.get('state') -%}, {{ comp_addr.state }}{%- endif -%}
                </div>
                {% if comp_addr.get('country') %}<div>{{ comp_addr.country }}</div>{% endif %}
                {% if comp_addr.get('pincode') %}<div>{{ comp_addr.pincode }}</div>{% endif %}
            {% endif %}

            {% if company_info.get('tax_id') %}
            <div><strong>GSTIN:</strong> {{ company_info.tax_id }}</div>
            {% endif %}
        </div>

        {# Invoice Dates #}
        <div class="invoice-dates">
            <div class="date-row">
                <span class="date-label">Invoice Date:</span>
                <span class="date-value">{{ frappe.utils.formatdate(doc.bill_date or doc.posting_date, "dd/MM/yyyy") }}</span>
            </div>
            <div class="date-row">
                <span class="date-label">Terms:</span>
                <span class="date-value">{{ doc.payment_terms_template or "Due on Receipt" }}</span>
            </div>
            <div class="date-row">
                <span class="date-label">Due Date:</span>
                <span class="date-value">{{ frappe.utils.formatdate(doc.due_date, "dd/MM/yyyy") if doc.due_date else "-" }}</span>
            </div>
        </div>
    </div>

    <!-- Items Table -->
    <table class="items-table">
        <thead>
            <tr>
                <th style="width: 20%;">Item</th>
                <th style="width: 25%;">Description</th>
                <th class="text-right" style="width: 10%;">Qty</th>
                <th class="text-right" style="width: 15%;">HSN/SAC</th>
                <th class="text-right" style="width: 15%;">Rate</th>
                <th class="text-right" style="width: 15%;">Taxable Value</th>
            </tr>
        </thead>
        <tbody>
            {% for item in doc.items %}
            <tr>
                <td>{{ item.item_code }}</td>
                <td>{{ item.item_name or "-" }}</td>
                <td class="text-right">{{ item.qty }}</td>
                <td class="text-right">{{ item.gst_hsn_code or "-" }}</td>
                <td class="text-right">‚Çπ{{ "{:,.2f}".format(item.rate) }}</td>
                <td class="text-right">‚Çπ{{ "{:,.2f}".format(item.amount) }}</td>
            </tr>
            {% endfor %}
        </tbody>
    </table>

    <!-- Tax Summary -->
    <div class="tax-summary">
        {% if doc.taxes %}
            {% for tax in doc.taxes %}
            <div class="tax-row">
                <span>{{ tax.description }}:</span>
                <span>‚Çπ{{ "{:,.2f}".format(tax.tax_amount) }}</span>
            </div>
            {% endfor %}
        {% endif %}

        <div class="tax-row total">
            <span>Total</span>
            <span>‚Çπ{{ "{:,.2f}".format(doc.grand_total) }}</span>
        </div>
    </div>

    <!-- Footer / Signature - SUPPLIER SIGNATURE -->
    <div class="footer">
        <div class="for-company">For {{ doc.supplier_name }}</div>

        <div class="signature-space"></div>

        <div class="signature-line"></div>

        <div class="signatory-name">Authorized Signatory</div>
    </div>
</div>

