body {
    font-family: Arial, sans-serif;
    font-size: 10pt;
    margin: 0;
    padding: 0;
}

.print-format {
    padding: 15px;
    max-width: 210mm;
    margin: 0 auto;
}

.header-section {
    margin-bottom: 15px;
    padding-bottom: 8px;
    border-bottom: 2px solid #333;
}

.header-content {
    display: flex;
    justify-content: space-between;
    align-items: flex-start;
}

.company-logo {
    width: 60px;
    height: 60px;
    margin-right: 15px;
}

.company-logo img {
    max-width: 60px;
    max-height: 60px;
    object-fit: contain;
}

.company-info {
    text-align: right;
    flex: 1;
}

.company-name {
    font-size: 14pt;
    font-weight: bold;
    margin-bottom: 2px;
}

.company-contact {
    font-size: 9pt;
    margin: 1px 0;
}

.title-status-section {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin: 12px 0 8px 0;
}

.invoice-title {
    font-size: 13pt;
    font-weight: bold;
}

.status-display {
    text-align: right;
}

.status-display .status-label {
    font-size: 10pt;
    font-weight: bold;
    text-transform: uppercase;
}

.invoice-details {
    margin-bottom: 12px;
    font-size: 9pt;
}

.invoice-details-row {
    display: flex;
    margin: 2px 0;
}

.invoice-details-label {
    font-weight: bold;
    width: 110px;
}

.invoice-details-value {
    flex: 1;
}

.print-no {
    font-size: 8pt;
    margin: 2px 0;
}

.items-table {
    width: 100%;
    border-collapse: collapse;
    margin: 12px 0;
    font-size: 9pt;
}

.items-table thead {
    background-color: #e8e8e8;
}

.items-table th,
.items-table td {
    border: 1px solid #999;
    padding: 5px 6px;
    text-align: left;
}

.items-table th {
    font-weight: bold;
    text-align: center;
}

.items-table .text-right {
    text-align: right;
}

.items-table .text-center {
    text-align: center;
}

.qty-column {
    width: 50px;
}

.rate-column {
    width: 80px;
}

.tax-column {
    width: 70px;
}

.disc-column {
    width: 70px;
}

.units-column {
    width: 80px;
}

.total-column {
    width: 80px;
}

.showing-info {
    font-size: 8pt;
    font-style: italic;
    margin: 5px 0 10px 0;
}

.totals-section {
    margin: 10px 0 15px 0;
    text-align: right;
}

.total-row {
    display: flex;
    justify-content: flex-end;
    margin: 3px 0;
    font-size: 9pt;
}

.total-label {
    width: 130px;
    text-align: right;
    padding-right: 15px;
    font-weight: bold;
}

.total-value {
    width: 100px;
    text-align: right;
}

.grand-total-row {
    font-size: 13pt;
    font-weight: bold;
    border-top: 2px solid #333;
    padding-top: 4px;
    margin-top: 6px;
}

.cash-customer-section {
    margin: 15px 0;
    padding: 8px 0;
    border-top: 1px solid #ccc;
    border-bottom: 1px solid #ccc;
}

.cash-customer-row {
    display: flex;
    margin: 3px 0;
    font-size: 9pt;
}

.cash-customer-label {
    font-weight: bold;
    width: 120px;
}

.cash-customer-value {
    flex: 1;
}
